class Monster{constructor(s,t,i,h,e,l){this.size=s,this.color=t,this.x=i,this.y=h,this.vx=e,this.vy=l}draw(s){s.save(),s.translate(this.x,this.y),s.beginPath(),s.rect(-this.size/2,-this.size/2,this.size,this.size),s.fillStyle=this.color,s.fill(),s.closePath(),s.restore(),this.x+=this.vx,this.y+=this.vy}}