class Robot{constructor(t,s,e,i,h){this.radius=t,this.color=s,this.screenWidth=e,this.screenHeight=i,this.position=h}setAngle(t){this.angle=t}draw(t){t.save(),t.translate(this.screenWidth/2,this.screenHeight/2),t.rotate(this.angle),t.beginPath(),t.arc(0,this.position,this.radius,0,2*Math.PI),t.fillStyle=this.color,t.fill(),t.closePath(),t.restore()}}class Light{constructor(t,s,e){this.color=t,this.screenWidth=s,this.screenHeight=e}setAngle(t){this.angle=t}draw(t){t.save(),t.translate(this.screenWidth/2,this.screenHeight/2),t.rotate(this.angle),t.beginPath(),t.lineTo(-500,this.screenWidth+100),t.lineTo(500,this.screenWidth+100),t.lineTo(0,0),t.fillStyle=this.color,t.fill(),t.closePath(),t.restore()}}